/** @format */

import React, { Component } from "react";
import Input from "./common/input";

class LoginForm extends Component {
  state = {
    /*null and undefined cannot be used as a value of control elements
      /initialize the properties of your state object, either to an empty string or some value that you get from a server*/
    account: {
      username: "",
      password: "",
      errors: {},
    },
  };

  validate = () => {
    const errors = {};
    const { account } = this.state;
    if (account.username.trim() === "")
      errors.username = "username is required";
    if (account.password.trim() === "")
      errors.password = "password is required";
    return Object.keys(errors).length === 0 ? null : errors;
  };
  handleSubmit = (e) => {
    e.preventDefault();
    const errors = this.validate();
    this.setState({ errors: errors || {} });
    if (errors) return;
  };
  handleChange = ({ currentTarget: input }) => {
    const account = {
      ...this.state.account,
    };
    //whenever you are working with the objects dynamically, use the bracket notation instead of the dot notation
    account[input.name] = input.value;
    this.setState({ account });
  };
  render() {
    const { account, errors } = this.state;

    return (
      <div>
        <h1>Login</h1>
        <form onSubmit={this.handleSubmit}>
          <Input
            name="username"
            value={account.username}
            label="Username"
            onChange={this.handleChange}
            error={account.errors.username}
          />
          <Input
            name="password"
            value={account.password}
            label="Password"
            onChange={this.handleChange}
            error={account.errors.password}
          />

          <button className="btn btn-primary">Login</button>
        </form>
      </div>
    );
  }
}

export default LoginForm;
